import React, { Component } from "react"; //imrc
class Counter extends Component {
  //cc
  state = {
    count: 0,
  };
  render() {
    return (
      <div>
        <span>{this.formatCount()}</span>
        <button>按钮1</button>
      </div>
    );
  }
  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}

export default Counter;
