import React, { Component } from "react"; //imrc
class Counter extends Component {
  //cc
  state = {
    count: 1,
    imgUrl: "./img/115.jpg",
  };
  style = {
    fontSize: 5,
    fontWeight: "bold",
  };
  imgStyle = {
    width: 500,
    height: "auto",
  };
  render() {
    return (
      <div>
        <span className={this.judgeNum()}>{this.formatCount()}</span>
        <button className="btn btn-secondary btn-sm">按钮1</button>
        <br />
        <img src={this.state.imgUrl} style={this.imgStyle} alt="1"></img>
        <br />
        <u style={this.style}>最进欲望很大</u>
      </div>
    );
  }
  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
  judgeNum() {
    let classes = "badge m-2 badge-";
    classes += this.state.count === 0 ? "warning" : "primary";
    return classes;
  }
}

export default Counter;
