import React, { Component } from "react"; //imrc
class Counter extends Component {
  //cc
  state = {
    count: 0,
    imgUrl: "./img/115.jpg",
  };
  style = {
    fontSize: 5,
    fontWeight: "bold",
  };
  imgStyle = {
    width: 500,
    height: "auto",
  };
  render() {
    return (
      <div>
        <span className="badge badge-primary m-2">{this.formatCount()}</span>
        <button className="btn btn-secondary btn-sm">按钮1</button>
        <br />
        <img
          src={this.state.imgUrl}
          style={this.imgStyle}
          alt="1"
          class="img-rounded"
        ></img>
        <br />
        <u style={this.style}>最进欲望很大</u>
      </div>
    );
  }
  formatCount() {
    const { count } = this.state;
    return count === 0 ? <h1>Zero</h1> : count;
  }
}

export default Counter;
