import React, { Component } from "react"; //imrc
class Counter extends Component {
  //cc
  state = {
    tags: ["tag1", "tag2", "tag3"],
  };

  render() {
    return <div>{this.rendertags()}</div>;
  }
  rendertags() {
    if (this.tags.length === 0) return <p>there is no tags</p>;
    return (
      <ul>
        {this.state.tags.map((tag) => (
          <li key={tag}>{tag}</li>
        ))}
      </ul>
    );
  }
}

export default Counter;
